# 1. Inheritance: Child class inherits from parent

# 2. Parent class
class Animal:
    def __init__(self, name):
        self.name = name
    def speak(self):
        return "Some sound"

# Child class
class Cat(Animal):
    # 3. Override method
    def speak(self):
        return f"{self.name} says Meow!"

cat = Cat("Whiskers")
print(cat.speak())  # Whiskers says Meow!

# 1. Exceptions: Errors during execution

# 2. Try-except
try:
    result = 10 / 0
except:
    print("An error occurred")

# 3. Specific exception
try:
    num = int("abc")
except ValueError:
    print("Invalid number format")
except ZeroDivisionError:
    print("Cannot divide by zero")
finally:
    print("This always runs")
